import React, { useState } from 'react';
import axios from 'axios';

const initialPlayer = {
  nickname: '',
  champion: '',
  k: 0,
  d: 0,
  a: 0,
  win: true,
  perfect: false
};

export default function Admin() {
  const [matchDate, setMatchDate] = useState('');
  const [teamBlue, setTeamBlue] = useState([{ ...initialPlayer }]);
  const [teamRed, setTeamRed] = useState([{ ...initialPlayer }]);

  const handleChange = (teamSetter, team, index, field, value) => {
    const updated = [...team];
    updated[index][field] = field === 'perfect' ? value.target.checked : value;
    teamSetter(updated);
  };

  const addPlayer = (teamSetter, team) => {
    teamSetter([...team, { ...initialPlayer }]);
  };

  const submitData = async () => {
    try {
      const matchData = {
        matchDate,
        teamBlue: teamBlue.map(p => ({
          ...p,
          k: parseInt(p.k),
          d: parseInt(p.d),
          a: parseInt(p.a),
          win: p.win === '승' || p.win === true,
        })),
        teamRed: teamRed.map(p => ({
          ...p,
          k: parseInt(p.k),
          d: parseInt(p.d),
          a: parseInt(p.a),
          win: p.win === '승' || p.win === true,
        })),
      };

      const res = await axios.post('http://localhost:5000/api/match/save', matchData);
      alert('✅ 저장 완료: ' + res.data.message);
    } catch (err) {
      console.error(err);
      alert('❌ 저장 실패: ' + err.message);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>✏ 전적 입력 (관리자 전용)</h2>

      <label>날짜: </label>
      <input type="date" value={matchDate} onChange={e => setMatchDate(e.target.value)} />

      <h3>🔵 블루팀</h3>
      <div className="grid grid-cols-[130px_120px_60px_60px_60px_60px_80px] font-bold text-sm mb-2">
  <div>소환사명</div>
  <div>챔피언</div>
  <div>결과</div>
  <div>Kills</div>
  <div>Deaths</div>
  <div>Assists</div>
  <div>Perfect</div>
</div>

      
      {teamBlue.map((player, idx) => (
        <div
          key={idx}
          className="grid grid-cols-[120px_100px_80px_60px_60px_60px_80px] gap-2 mb-2 items-center"
        >
          <input
            type="text"
            className="w-full border rounded px-2 py-1"
            value={player.nickname}
            onChange={(e) => handleChange(setTeamBlue, teamBlue, idx, 'nickname', e.target.value)}
          />
          <input
            type="text"
            className="w-full border rounded px-2 py-1"
            value={player.champion}
            onChange={(e) => handleChange(setTeamBlue, teamBlue, idx, 'champion', e.target.value)}
          />
          <select
            className="w-full border rounded px-2 py-1"
            value={player.result}
            onChange={(e) => handleChange(setTeamBlue, teamBlue, idx, 'result', e.target.value)}
          >
            <option value="승">승</option>
            <option value="패">패</option>
          </select>
          <input
            type="number"
            className="w-full border rounded px-2 py-1"
            value={player.k}
            onChange={(e) => handleChange(setTeamBlue, teamBlue, idx, 'k', Number(e.target.value))}
          />
          <input
            type="number"
            className="w-full border rounded px-2 py-1"
            value={player.d}
            onChange={(e) => handleChange(setTeamBlue, teamBlue, idx, 'd', Number(e.target.value))}
          />
          <input
            type="number"
            className="w-full border rounded px-2 py-1"
            value={player.a}
            onChange={(e) => handleChange(setTeamBlue, teamBlue, idx, 'a', Number(e.target.value))}
          />
          <label className="flex items-center gap-1">
            <input
              type="checkbox"
              checked={player.perfect}
              onChange={(e) => handleChange(setTeamBlue, teamBlue, idx, 'perfect', e.target.checked)}
            />
            퍼펙트
          </label>
        </div>
      ))}
      <button onClick={() => addPlayer(setTeamBlue, teamBlue)}>+ 블루 추가</button>

      <h3>🔴 레드팀</h3>
      {teamRed.map((player, idx) => (
        <div key={idx} style={{ border: '1px solid #ccc', padding: '10px', marginBottom: '10px' }}>
          <input placeholder="소환사명" value={player.nickname} onChange={e => handleChange(setTeamRed, teamRed, idx, 'nickname', e.target.value)} />
          <input placeholder="챔피언" value={player.champion} onChange={e => handleChange(setTeamRed, teamRed, idx, 'champion', e.target.value)} />
          <select value={player.win ? '승' : '패'} onChange={e => handleChange(setTeamRed, teamRed, idx, 'win', e.target.value === '승')}>
            <option>승</option>
            <option>패</option>
          </select>
          <input type="number" placeholder="킬" value={player.k} onChange={e => handleChange(setTeamRed, teamRed, idx, 'k', e.target.value)} />
          <input type="number" placeholder="데스" value={player.d} onChange={e => handleChange(setTeamRed, teamRed, idx, 'd', e.target.value)} />
          <input type="number" placeholder="어시스트" value={player.a} onChange={e => handleChange(setTeamRed, teamRed, idx, 'a', e.target.value)} />
          <label>
            <input type="checkbox" checked={player.perfect} onChange={e => handleChange(setTeamRed, teamRed, idx, 'perfect', e)} /> 퍼펙트
          </label>
        </div>
      ))}
      <button onClick={() => addPlayer(setTeamRed, teamRed)}>+ 레드 추가</button>

      <br /><br />
      <button onClick={submitData}>💾 저장</button>
    </div>
  );
}
