import React, { useState, useEffect } from 'react';
import './index.css';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import Admin from './pages/Admin';
import Search from './pages/Search';
import Login from './pages/Login';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(() => {
    return localStorage.getItem('ziklolgg_login') === 'true';
  });

  const handleLogin = (id, pw) => {
    if (id === 'zikops' && pw === '2025') {
      localStorage.setItem('ziklolgg_login', 'true');
      setIsLoggedIn(true);
      return true;
    }
    return false;
  };

  const handleLogout = () => {
    localStorage.removeItem('ziklolgg_login');
    setIsLoggedIn(false);
  };

  return (
    <Router>
      {/* ÏÉÅÎã®Î∞î */}
      <header className="bg-purple-700 text-white font-bold text-lg py-4 px-6 shadow">
        ZIKLOL.GG - ÏÜåÌôòÏÇ¨ Ï†ÑÏ†Å ÏãúÏä§ÌÖú
      </header>

      {/* ÎÑ§ÎπÑÍ≤åÏù¥ÏÖò Î∞î */}
      <nav className="flex flex-wrap items-center gap-4 px-6 py-3 bg-purple-100 text-purple-700 font-medium text-sm">
        <Link to="/search" className="hover:underline">üîç Í≤ÄÏÉâ</Link>

        {isLoggedIn ? (
          <>
            <Link to="/admin" className="hover:underline">‚úè ÏûÖÎ†•</Link>
            <button
              onClick={handleLogout}
              className="bg-red-100 text-red-600 px-3 py-1 rounded hover:bg-red-200 transition"
            >
              Î°úÍ∑∏ÏïÑÏõÉ
            </button>
          </>
        ) : (
          <Link to="/login" className="hover:underline">Î°úÍ∑∏Ïù∏</Link>
        )}
      </nav>

      {/* ÌéòÏù¥ÏßÄ ÎÇ¥Ïö© */}
      <main className="p-6">
        <Routes>
          <Route path="/search" element={<Search />} />
          <Route
            path="/admin"
            element={isLoggedIn ? <Admin /> : <Navigate to="/login" />}
          />
          <Route path="/login" element={<Login onLogin={handleLogin} />} />
          <Route path="*" element={<Navigate to="/search" />} />
        </Routes>
      </main>
    </Router>
  );
}

export default App;
