import React, { useState } from 'react';
import axios from 'axios';

const initialPlayer = {
  nickname: '',
  champion: '',
  k: 0,
  d: 0,
  a: 0,
  win: true,
  perfect: false
};

export default function Admin() {
  const [matchDate, setMatchDate] = useState('');
  const [teamBlue, setTeamBlue] = useState([{ ...initialPlayer }]);
  const [teamRed, setTeamRed] = useState([{ ...initialPlayer }]);

  const handleChange = (teamSetter, team, index, field, value) => {
    const updated = [...team];
    updated[index][field] = field === 'perfect' ? value.target.checked : value;
    teamSetter(updated);
  };

  const addPlayer = (teamSetter, team) => {
    teamSetter([...team, { ...initialPlayer }]);
  };

  const submitData = async () => {
    try {
      const matchData = {
        matchDate,
        teamBlue: teamBlue.map(p => ({
          ...p,
          k: parseInt(p.k),
          d: parseInt(p.d),
          a: parseInt(p.a),
          win: p.win === 'Ïäπ' || p.win === true,
        })),
        teamRed: teamRed.map(p => ({
          ...p,
          k: parseInt(p.k),
          d: parseInt(p.d),
          a: parseInt(p.a),
          win: p.win === 'Ïäπ' || p.win === true,
        })),
      };

      const res = await axios.post('http://localhost:5000/api/match/save', matchData);
      alert('‚úÖ Ï†ÄÏû• ÏôÑÎ£å: ' + res.data.message);
    } catch (err) {
      console.error(err);
      alert('‚ùå Ï†ÄÏû• Ïã§Ìå®: ' + err.message);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>‚úè Ï†ÑÏ†Å ÏûÖÎ†• (Í¥ÄÎ¶¨Ïûê Ï†ÑÏö©)</h2>

      <label>ÎÇ†Ïßú: </label>
      <input type="date" value={matchDate} onChange={e => setMatchDate(e.target.value)} />

      <h3>üîµ Î∏îÎ£®ÌåÄ</h3>
      <div style={{ display: 'flex', gap: '10px', marginBottom: '5px', fontWeight: 'bold' }}>
  <span style={{ width: '100px' }}>ÏÜåÌôòÏÇ¨Î™Ö</span>
  <span style={{ width: '80px' }}>Ï±îÌîºÏñ∏</span>
  <span style={{ width: '60px' }}>Í≤∞Í≥º</span>
  <span style={{ width: '60px' }}>Kills</span>
  <span style={{ width: '60px' }}>Deaths</span>
  <span style={{ width: '80px' }}>Assists</span>
  <span style={{ width: '80px' }}>Perfect</span>
</div>
      {teamBlue.map((player, idx) => (
        <div key={idx} style={{ border: '1px solid #ccc', padding: '10px', marginBottom: '10px' }}>
          <input placeholder="ÏÜåÌôòÏÇ¨Î™Ö" value={player.nickname} onChange={e => handleChange(setTeamBlue, teamBlue, idx, 'nickname', e.target.value)} />
          <input placeholder="Ï±îÌîºÏñ∏" value={player.champion} onChange={e => handleChange(setTeamBlue, teamBlue, idx, 'champion', e.target.value)} />
          <select value={player.win ? 'Ïäπ' : 'Ìå®'} onChange={e => handleChange(setTeamBlue, teamBlue, idx, 'win', e.target.value === 'Ïäπ')}>
            <option>Ïäπ</option>
            <option>Ìå®</option>
          </select>
          <input type="number" placeholder="ÌÇ¨" value={player.k} onChange={e => handleChange(setTeamBlue, teamBlue, idx, 'k', e.target.value)} />
          <input type="number" placeholder="Îç∞Ïä§" value={player.d} onChange={e => handleChange(setTeamBlue, teamBlue, idx, 'd', e.target.value)} />
          <input type="number" placeholder="Ïñ¥ÏãúÏä§Ìä∏" value={player.a} onChange={e => handleChange(setTeamBlue, teamBlue, idx, 'a', e.target.value)} />
          <label>
            <input type="checkbox" checked={player.perfect} onChange={e => handleChange(setTeamBlue, teamBlue, idx, 'perfect', e)} /> ÌçºÌéôÌä∏
          </label>
        </div>
      ))}
      <button onClick={() => addPlayer(setTeamBlue, teamBlue)}>+ Î∏îÎ£® Ï∂îÍ∞Ä</button>

      <h3>üî¥ Î†àÎìúÌåÄ</h3>
      {teamRed.map((player, idx) => (
        <div key={idx} style={{ border: '1px solid #ccc', padding: '10px', marginBottom: '10px' }}>
          <input placeholder="ÏÜåÌôòÏÇ¨Î™Ö" value={player.nickname} onChange={e => handleChange(setTeamRed, teamRed, idx, 'nickname', e.target.value)} />
          <input placeholder="Ï±îÌîºÏñ∏" value={player.champion} onChange={e => handleChange(setTeamRed, teamRed, idx, 'champion', e.target.value)} />
          <select value={player.win ? 'Ïäπ' : 'Ìå®'} onChange={e => handleChange(setTeamRed, teamRed, idx, 'win', e.target.value === 'Ïäπ')}>
            <option>Ïäπ</option>
            <option>Ìå®</option>
          </select>
          <input type="number" placeholder="ÌÇ¨" value={player.k} onChange={e => handleChange(setTeamRed, teamRed, idx, 'k', e.target.value)} />
          <input type="number" placeholder="Îç∞Ïä§" value={player.d} onChange={e => handleChange(setTeamRed, teamRed, idx, 'd', e.target.value)} />
          <input type="number" placeholder="Ïñ¥ÏãúÏä§Ìä∏" value={player.a} onChange={e => handleChange(setTeamRed, teamRed, idx, 'a', e.target.value)} />
          <label>
            <input type="checkbox" checked={player.perfect} onChange={e => handleChange(setTeamRed, teamRed, idx, 'perfect', e)} /> ÌçºÌéôÌä∏
          </label>
        </div>
      ))}
      <button onClick={() => addPlayer(setTeamRed, teamRed)}>+ Î†àÎìú Ï∂îÍ∞Ä</button>

      <br /><br />
      <button onClick={submitData}>üíæ Ï†ÄÏû•</button>
    </div>
  );
}
