import React, { useState } from 'react';
import axios from 'axios';

function Search() {
  const [nickname, setNickname] = useState('');
  const [records, setRecords] = useState([]);
  const [filteredRecords, setFilteredRecords] = useState([]);
  const [champions, setChampions] = useState([]);
  const [selectedChampion, setSelectedChampion] = useState('');
  const [message, setMessage] = useState('');

  const handleSearch = async () => {
    if (!nickname.trim()) {
      setMessage('ÎãâÎÑ§ÏûÑÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    try {
      const res = await axios.get(`/api/match/player/${nickname.trim()}`);
      const data = res.data;

      setRecords(data);
      setFilteredRecords(data);
      setMessage('');

      // Ï±îÌîºÏñ∏ Î™©Î°ù Ï§ëÎ≥µ Ï†úÍ±∞
      const champList = [...new Set(data.map((r) => r.champion))];
      setChampions(champList);
      setSelectedChampion('');
    } catch (error) {
      console.error(error);
      setMessage('Í≤ÄÏÉâ Ïã§Ìå®. ÏÑúÎ≤Ñ Ïò§Î•ò ÎòêÎäî Ìï¥Îãπ ÏÜåÌôòÏÇ¨Í∞Ä ÏóÜÏäµÎãàÎã§.');
    }
  };

  const handleChampionChange = (e) => {
    const selected = e.target.value;
    setSelectedChampion(selected);

    if (selected === '') {
      setFilteredRecords(records);
    } else {
      setFilteredRecords(records.filter((r) => r.champion === selected));
    }
  };

  const renderRating = (k, a, d) => {
    if (typeof k !== 'number' || typeof a !== 'number' || typeof d !== 'number') return 'N/A';
    if (d === 0) return (k + a).toFixed(2);
    return ((k + a) / d).toFixed(2);
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>üîç ÏÜåÌôòÏÇ¨ Ï†ÑÏ†Å Í≤ÄÏÉâ</h2>
      <input
        type="text"
        value={nickname}
        onChange={(e) => setNickname(e.target.value)}
        placeholder="ÏÜåÌôòÏÇ¨Î™Ö ÏûÖÎ†•"
        className="flex-1 px-4 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-purple-500"
      />
      <button onClick={handleSearch}
      className="px-5 py-2 bg-purple-600 text-white font-semibold rounded-md hover:bg-purple-700 transition"
      >Í≤ÄÏÉâ</button>

      {message && <p style={{ color: 'red' }}>{message}</p>}

      {/* Ï±îÌîºÏñ∏ ÌïÑÌÑ∞ ÎìúÎ°≠Îã§Ïö¥ */}
      {champions.length > 0 && (
        <div style={{ marginTop: '10px' }}>
          <label>Ï±îÌîºÏñ∏ ÌïÑÌÑ∞: </label>
          <select value={selectedChampion} onChange={handleChampionChange}>
            <option value="">Ï†ÑÏ≤¥ Î≥¥Í∏∞</option>
            {champions.map((champ) => (
              <option key={champ} value={champ}>{champ}</option>
            ))}
          </select>
        </div>
      )}

      <div style={{ marginTop: '20px' }}>
        {filteredRecords.map((r, index) => (
          <div key={index} style={{ background: '#e3fce3', padding: '10px', marginBottom: '10px' }}>
            <strong>{r.nickname}</strong> - {r.champion} ({r.matchDate})
            <br />
            {r.win ? 'ÏäπÎ¶¨' : 'Ìå®Î∞∞'} | ÌçºÌéôÌä∏: {r.perfect ? 'üåü' : '‚Äî'}
            <br />
            KDA: {r.k} / {r.d} / {r.a} | ÌèâÏ†ê: <strong>{renderRating(r.k, r.a, r.d)}</strong>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Search;
